import React, { useState } from "react";

export default function Layout() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false); // Shared state

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen); // Toggle the state
  };

  return (
    <div>
      <Header toggleSidebar={toggleSidebar} isSidebarOpen={isSidebarOpen} />
      <Sidebar isSidebarOpen={isSidebarOpen} toggleSidebar={toggleSidebar} />
      {/* Rest of your application content here */}
    </div>
  );
}

function Header({ toggleSidebar, isSidebarOpen }) {
  return (
    <header style={styles.header}>
      <button onClick={toggleSidebar}>
        {isSidebarOpen ? "Close Menu" : "Open Menu"}
      </button>
      <h3>This is header</h3>
      <AuthButton />
    </header>
  );
}

function Sidebar({ isSidebarOpen, toggleSidebar }) {
  return (
    <aside>
      <button onClick={toggleSidebar}>Close Menu</button>
      <nav>
        <ul>
          <li>
            <Link href="/">home</Link>
          </li>
          <li>
            <Link href="/scheduler">Scheduler</Link>
          </li>
        </ul>
      </nav>
      <AuthButton />
    </aside>
  );
}

function AuthButton() {
  return <button>Login</button>;
}

function Link({ href, children }) {
  return (
    <a href={href} style={styles.link}>
      {children}
    </a>
  );
}

const styles = {
  layout: {
    display: "flex",
    flexDirection: "column",
    height: "100vh",
  },
  header: {
    backgroundColor: "#f8f9fa",
    padding: "10px",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
  sidenav: {
    width: "250px",
    height: "100%",
    backgroundColor: "#343a40",
    color: "white",
    padding: "20px",
    position: "fixed",
    left: "-250px",
    transition: "left 0.3s",
  },
  open: {
    left: "0",
  },
  link: {
    color: "white",
    textDecoration: "none",
  },
};
